import "./App.css";
import { useState } from "react";

function App() {
  const [count, setCount] = useState(0);

  const onClick = () => {
    const newVal = count + 1;
    setCount(newVal);
    localStorage.setItem("count", newVal);
  }
  return (
    <>
      <div className="main-counter" id="main-counter">
        {count}
      </div>
      <button id="button" onClick={() => setCount((count) => count + 1)}>
        Click Me
      </button>
    </>
  );
}

export default App;
